var UseDates = {
campaign1: {date: "12/7/2018 23:59:59", message: "standard"},
campaign2: {date: "12/19/2018 23:59:59", message: "2-Day"},
campaign3: {date: "12/20/2018 23:59:59", message: "Next Day"},
displayMessage: false,
getUseDate: function (today) {
var c1 = new Date(this.campaign1.date);
var c2 = new Date(this.campaign2.date);
var c3 = new Date(this.campaign3.date);
if (today <= c1) {
this.displayMessage = this.campaign1.message;
return c1;
}
if (today > c1 && today <= c2) {
this.displayMessage = this.campaign2.message;
return c2;
}
if (today > c2 && today <= c3) {
this.displayMessage = this.campaign3.message;
return c3;
}
return false;
}
}
