function getDaysLeft() {
var oneDay = 24 * 60 * 60 * 1000; // hours*minutes*seconds*milliseconds
var today = new Date();
var dateToUse = UseDates.getUseDate(today);
if (typeof dateToUse != 'object') {
return false;
}
var days = (Math.ceil(Math.abs((today.getTime() - dateToUse.getTime()) / (oneDay))) - 1);
var displayText = {
last: '<span id="remainingText">LAST DAY</span>',
single: '<span id="remainingText"> DAY LEFT</span>',
standard: '<span id="remainingText"> DAYS LEFT</span>'
};
if (days === 0) {
return displayText.last;
}
if (days === 1) {
return 1 + displayText.single
}
return days + displayText.standard;
}
