function locationSubmit(address, event) {
if(address != '') {
if (typeof google != 'undefined') {
geocoder = new google.maps.Geocoder();
geocoder.geocode({'address': address}, function (results, status) {
if (status == google.maps.GeocoderStatus.OK) {
jQuery("#storeLocatorMiniFormPopup").submit();
} else {
displayInputError();
}
});
} else {
jQuery("#storeLocatorMiniFormPopup").submit();
}
} else {
displayInputError();
}
}
function displayInputError() {
jQuery("#zipcode_input_popup").val('');
alert("Please make sure Your Address is a valid US address.");
}
function loadGoogleMapsScript() {
if ((googleMapsScripLoaded == 0)
&& (window.location.pathname != '/storelocator/location/map')
&& (window.location.pathname != '/storelocator/location/search')) {
var script = document.createElement("script");
script.type = "text/javascript";
script.src = "https://maps.googleapis.com/maps/api/js?client=gme-harborfreighttools&v=3&libraries=places&async=2&callback=initialize_nostorelocator";
document.body.appendChild(script);
googleMapsScripLoaded = 1;
} else {
initialize_nostorelocator();
}
}

