function initAutoComplete(){
if (!window.EAComplete) return;
document.getElementById('search_mini_form').onsubmit = trimResultCount;
EAComplete.init({
url : '/catalogsearch/autoComplete',
id: 'searchInputField',
dict: 'Ecomm',
num: 10,
submitFctn: function() { submitSearchForm(); } });
}
if(document.getElementById('search_mini_form') != null){
if (window.attachEvent) window.attachEvent('onload',initAutoComplete); else window.addEventListener("load",initAutoComplete,false);
}
function submitSearchForm(){
trimResultCount();
document.getElementById('search_mini_form').submit();
}
function trimResultCount(){
var searchText = document.getElementById('searchInputField').value;
var searchTextArray = searchText.split(" ");
if(searchTextArray.length > 1 || searchText.indexOf('\t') != -1){
searchText = searchText.replace('\t','');
searchText = searchText.replace('\t',' ');
var searchArray = searchText.split(" ");
if(isNumeric(searchArray[searchArray.length-1])){
searchArray.pop();
var newSearchText = searchArray.join(" ");
var element =  document.getElementById('search');
if (typeof(element) != 'undefined' && element != null) {
element.value = newSearchText;
}
}
}
}
