if (videoOverlayEnabled && $j.cookie(videoCookieName) != cookieValue && $j('body.mw_home').length == 0) {
var secure = (window.location.protocol == "https:");
$j.cookie(videoCookieName, cookieValue, {
expires:14,
secure: secure
});
setTimeout(function () {
$j(videoElement).colorbox({
inline: true,
href: $j("#videooverlay"),
width: "740px",
open: true
});
}, 10);
displayingVideoOverlay = true;
window.trackEvar(32, 'Displaying Video Overlay', Date.now())
}
});
$j(document).keyup(function (e) {
if (e.keyCode == 27) {
CloseDialog();
displayingVideoOverlay = false;
}
});

