var welcomeoverlayForm = new VarienForm('welcomeoverlayForm', true);
var $j = jQuery.noConflict();
var emailField = $j('#emailAddress');
var responseMessage = $j('#responseMessage');
$j(function ($j) {
if (!$j.cookie('new2hft') && (!window.videoOverlayEnabled || window.displayingVideoOverlay == false)) {
var secure = (window.location.protocol == "https:");
$j.cookie('new2hft', '1', {
expires:60,
secure: secure
});
$j('#container_welcome').removeClass('no-display');
$j('<div id="welcome_overlay"></div>').insertAfter('#container_welcome');
}
});
emailField.on('blur', function () {
if (emailField.hasClass('validation-failed')) return;
responseMessage.html('');
});
$j('#welcomeoverlayForm').submit(function () {
var email = emailField.val();
responseMessage.html('');
CallAddEmailWebService('/emailservice/index/quicksignup', email);
return false;
});
$j(document).keyup(function (e) {
if (e.keyCode == 27) {
CloseDialog();
}
});
